// @ Flow

import type { Player } from '../../src/scripts/types/index';
import Component from '../../src/scripts/Components/Component';
import PlayToggle from './Play-toggle';
import ProgressBar from './ProgressBar/index';

class ControlBar extends Component{
    constructor(player: Player, options: any = {}){
        super(player, options);

        this.el().classList.add("vjs-panorama__controlbar");

        this.addChild("playToggle", new PlayToggle(player, options));
        this.addChild("ProgressBar", new ProgressBar(player, options));
    }


}

export default ControlBar;