// @ flow

import Component from '../../src/scripts/Components/Component';

class ControlText extends Component{
    constructor(player: Player, options: {
        DefaultText: string;
    } = {}){
        super(player, options);
        let el = this.el();
        el.className = "vjs-panorama-control-text";
        el.innerText = options.DefaultText;
    }

    createEl(tagName: string, properties?: any, attributes?: any){
        return super.createEl("span");
    }
}

export default ControlText;