// @flow

import type { Player } from '../../src/scripts/types/index';
import Component from '../../src/scripts/Components/Component';

class PosterImage extends Component{
    constructor(player: Player, options: {
        posterSrc: string;
    }){
        super(player, options);

        this.el().classList.add("vjs-panorama__posterImage");

        this.el().style.backgroundImage = `url("${options.posterSrc}")`;
    }


}

export default PosterImage;